meditation_names = ["Mindfulness", "Metta (Loving-Kindness)", "Zen Meditation", 'Qigong (Chi Gong)', "Mantra Meditation (OM)", "Transcendental Meditation (TM)", "Vipassana Meditation", "Chakra Meditation", "Kundalini Meditation"]

